#include <iostream>#include <vector> using namespace std; void solve() {    int n;    cin >> n;    vector<int> a(n);    for (int i = 0; i < n; i++) cin >> a[i];     int idx2 = -1;    // Find at least one district that belongs to a different gang than District 0    for (int i = 1; i < n; i++) {        if (a[i] != a[0]) {            idx2 = i;            break;        }    }     if (idx2 == -1) {        cout << "NO" << endl;        return;    }     cout << "YES" << endl;    // Pass 1: Connect everything possible to District 0 (the primary hub)    // Pass 2: If a district has the same gang as 0, connect it to idx2 (the secondary hub)    for (int i = 1; i < n; i++) {        if (a[i] != a[0]) {            // District i is different from 0, connect them            cout << 1 << " " << i + 1 << endl;        } else {            // District i is same as 0, connect it to idx2             // (We know idx2 has a different gang than 0, and thus different than i)            cout << idx2 + 1 << " " << i + 1 << endl;        }    }} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin >> t;    while (t--) {        solve();    }    return 0;}