#include <iostream>using namespace std;typedef long long ll;Â int main() {    ll t;    cin >> t;    while(t--) {        ll n, k;        cin >> n >> k;                // 1. Move to 0-indexing (The Kotlin 'k--')        k--;                 ll floor = n / 2;                // 2. If n is odd, add the shift. If even, (n%2) makes this 0.        ll shift = (n % 2 == 0) ? 0 : (k / floor);                // 3. Calculate position and move back to 1-indexing        cout << (k + shift) % n + 1 << "\n";    }    return 0;}