#include <iostream>#include <vector>#include <map>#include <algorithm> using namespace std;typedef long long ll; void solve() {    ll n, m;    cin >> n >> m;    map<int, int> mp;    for (int i = 0; i < n; i++) {        int x; cin >> x;        mp[x % m]++;    }     ll ans = 0;    // We use a frequency array or check map properly to avoid iterator issues    for (auto const& [curr, count] : mp) {        if (mp[curr] == 0) continue; // Already processed this or its partner         if (curr == 0) {            ans++;         } else if (2 * curr == m) {            ans++;        } else {            int rem = m - curr;            int count_curr = mp[curr];            int count_rem = mp.count(rem) ? mp[rem] : 0;             // Mathematical grouping: 1 group for the balanced part,             // plus individual groups for the leftovers.            if (count_rem == 0) {                ans += count_curr;            } else {                ans += 1 + max(0, abs(count_curr - count_rem) - 1);            }            // Mark both as processed            mp[curr] = 0;            mp[rem] = 0;        }    }    cout << ans << "\n";} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t; cin >> t;    while (t--) solve();    return 0;}