#include <bits/stdc++.h>using namespace std; typedef long long ll;int MOD = 1e9 + 7;  // matrix rotate by 90 degree => a[i][j] = a[j][n-i-1] ll rs(ll l,ll r) {    if(l>r){        return 0;    }    // Using the Arithmetic Series formula: (n / 2) * (first + last)    return (l + r) * (r - l + 1) / 2;} ll unq(vector<ll> &a){    // prints only the unique elements    // removes the contigous duplicates and returns the new size    ll n = unique(a.begin(), a.end()) - a.begin();    return n;} ll __lcm(ll a, ll b){    return (a / __gcd(a, b)) * b;} void fast_io(){    ios::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);}  int main() {	// your code goes herell t;cin>>t;while(t--){    ll n;    cin>>n;    vector<ll>a(n);    for(ll i=0;i<n;i++) cin>>a[i];    unordered_map<ll,ll>mp;    for(ll i=0;i<n;i++){        mp[a[i]-(i+1)]++;    }    ll ans =0;    for(auto el : mp){        if(el.second>1){            ll x = el.second;            x = ((x)*(x-1))/2;            ans+=x;        }    }    cout<<ans<<"\n";}}