#include <iostream>#include <vector> using namespace std; void solve() {    int n;    cin >> n;        // pos[x] stores the 1-based index i where a[i] = x    // Max value of a[i] is 2*n    vector<long long> pos(2 * n + 1, 0);    for (int i = 1; i <= n; i++) {        int val;        cin >> val;        pos[val] = i;    }     long long count = 0;     // Iterate through possible values of a[i] and a[j]    // Let v1 = a[i] and v2 = a[j]    // Condition: v1 * v2 = pos[v1] + pos[v2]    // Since pos[v1] + pos[v2] <= 2*n, then v1 * v2 <= 2*n    for (long long v1 = 1; v1 * v1 <= 2 * n; v1++) {        if (pos[v1] == 0) continue; // Value v1 not in array         for (long long v2 = v1 + 1; v1 * v2 <= 2 * n; v2++) {            if (pos[v2] == 0) continue; // Value v2 not in array             if (v1 * v2 == pos[v1] + pos[v2]) {                count++;            }        }    }     cout << count << endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin >> t;    while (t--) {        solve();    }    return 0;}