#include <bits/stdc++.h>using namespace std; long long solve(string s, string c) {    int n = s.size();    bool flag1 = false; // Found the second digit (e.g., '5')    bool flag2 = false; // Found the first digit (e.g., '2')    int count = 0;     for (int i = n - 1; i >= 0; i--) {        if (!flag1) {            if (s[i] == c[1]) {                flag1 = true;            } else {                count++; // Digits to the right of the suffix            }        } else {            if (s[i] == c[0]) {                flag2 = true;                break; // We found both in the correct order            } else {                count++; // Digits between the two suffix digits            }        }    }     return flag2 ? count : LLONG_MAX;}int main() {	// your code goes here    long long t;    cin>>t;    while(t--){        long long n;        cin>>n;        string s = to_string(n);        vector<string> check = {"00","25","50","75"};        long long ans = LLONG_MAX;        for(int i=0;i<4;i++){            ans = min(ans,solve(s,check[i]));        }        cout<<ans<<endl;    }}