#include <iostream> using namespace std; void solve() {    long long x0, n;    if (!(cin >> x0 >> n)) return;     long long d = 0;        // Determine the displacement 'd' based on n % 4    switch (n % 4) {        case 0: d = 0; break;        case 1: d = n; break;        case 2: d = -1; break;        case 3: d = -n - 1; break;    }     // Apply displacement based on whether the starting position is even or odd    if (x0 % 2 == 0) {        cout << x0 - d << endl;    } else {        cout << x0 + d << endl;    }} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin >> t;    while (t--) {        solve();    }    return 0;}