#include <bits/stdc++.h>using namespace std; void check(vector<int>&a,bool &ans,int x){    vector<int>b;    int n = a.size();    for(int i=0;i<n;i++){        if(a[i]!=x){            b.push_back(a[i]);        }    }    int m= b.size();    for(int i=0;i<m;i++){        if(b[i]!=b[m-i-1]) return;    }    ans = true;} int main() {	// your code goes hereint t;cin>>t;while(t--){    int n;    cin>>n;    vector<int>a(n);    for(int i=0;i<n;i++){        cin>>a[i];    }    bool ans =true;    for(int i=0;i<n;i++){        if(a[i]!=a[n-i-1]){            ans = false;            check(a,ans,a[i]);            check(a,ans,a[n-i-1]);            break;        }    }    if(ans) cout<<"YES"<<endl;    else cout<<"NO"<<endl;}}