#include <bits/stdc++.h>using namespace std;typedef long long ll; void solve() {    ll n, x;    cin >> n >> x;    vector<ll> a(n);    for(int i = 0; i < n; i++) cin >> a[i];     sort(a.begin(), a.end());        vector<ll> pre(n);    pre[0] = a[0];    for(int i = 1; i < n; i++) pre[i] = pre[i-1] + a[i];     ll total_packs = 0;    ll current_day = 0;     // Iterate backwards from the maximum possible packs (n) to 1    for (int i = n - 1; i >= 0; i--) {        ll k = i + 1; // number of packs        // Cost of k packs on current_day: pre[i] + k * current_day        if (pre[i] + k * current_day <= x) {            // Calculate total days we can buy exactly k packs            ll max_day = (x - pre[i]) / k;            ll num_days = max_day - current_day + 1;                        total_packs += num_days * k;            current_day += num_days;        }    }    cout << total_packs << endl;} int main() {    ios::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin >> t;    while(t--) solve();    return 0;}