#include <bits/stdc++.h>using namespace std; typedef long long ll;int MOD = 1e9 + 7;  // matrix rotate by 90 degree => a[i][j] = a[j][n-i-1] ll rs(ll l,ll r) {    if(l>r){        return 0;    }    // Using the Arithmetic Series formula: (n / 2) * (first + last)    return (l + r) * (r - l + 1) / 2;} ll unq(vector<ll> &a){    // prints only the unique elements    // removes the contigous duplicates and returns the new size    ll n = unique(a.begin(), a.end()) - a.begin();    return n;} ll __lcm(ll a, ll b){    return (a / __gcd(a, b)) * b;} void fast_io(){    ios::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);} #include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int n, q;    cin >> n >> q;     vector<long long> a(n);    for (int i = 0; i < n; i++) cin >> a[i];     vector<long long> last(n, 0);   // last update time of a[i]    long long sum = 0;    for (auto x : a) sum += x;     long long base = 0;             // value from last type-2 query    long long global_time = 0;      // time of last type-2 query    long long timer = 0;     while (q--) {        timer++;        int t;        cin >> t;         if (t == 1) {            int i;            long long x;            cin >> i >> x;            i--; // 0-based             long long old;            if (last[i] < global_time)                old = base;            else                old = a[i];             sum = sum - old + x;            a[i] = x;            last[i] = timer;        }        else {            long long x;            cin >> x;            base = x;            global_time = timer;            sum = 1LL * n * x;        }         cout << sum << '\n';    }     return 0;}