#include<bits/stdc++.h>typedef long long ll;using namespace std;int main() {    ll t;    cin >> t;    while(t--){        ll n, s;        cin >> n >> s;         vector<ll> a(n);        ll sum = 0;        for(ll i = 0; i < n; i++){            cin >> a[i];            sum += a[i];        }         if(sum < s){            cout << -1 << "\n";            continue;        }         // prefix sum        vector<ll> pre(n);        pre[0] = a[0];        for(ll i = 1; i < n; i++){            pre[i] = pre[i - 1] + a[i];        }         ll ans = LLONG_MAX;         for(ll i = 0; i < n; i++){            ll lo = i, hi = n - 1;            ll pos = -1;             while(lo <= hi){                ll mid = lo + (hi - lo) / 2;                ll curr = pre[mid] - (i ? pre[i - 1] : 0);                 if(curr <= s){                    pos = mid;                    lo = mid + 1;                } else {                    hi = mid - 1;                }            }             if(pos == -1) continue;                        ll final_sum = pre[pos] - (i ? pre[i - 1] : 0);            if(final_sum != s) continue;             ans = min(ans, n - (pos - i + 1));        }         cout << (ans == LLONG_MAX ? -1 : ans) << "\n";    }}