#include <iostream>#include <vector> using namespace std; void solve() {    int n;    cin >> n;    vector<int> a(n);    int non_zero_segments = 0;    bool in_segment = false;     for (int i = 0; i < n; i++) {        cin >> a[i];                if (a[i] != 0) {            if (!in_segment) {                non_zero_segments++;                in_segment = true;            }        } else {            in_segment = false;        }    }     if (non_zero_segments == 0) {        cout << 0 << endl;    } else if (non_zero_segments == 1) {        cout << 1 << endl;    } else {        cout << 2 << endl;    }} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin >> t;    while (t--) {        solve();    }    return 0;}