#include <bits/stdc++.h>using namespace std; typedef long long ll;int MOD = 1e9 + 7; ll unq(vector<ll> &a){    // prints only the unique elements    // removes the contigous duplicates and returns the new size    ll n = unique(a.begin(), a.end()) - a.begin();    return n;} ll __lcm(ll a, ll b){    return (a / __gcd(a, b)) * b;} void fast_io(){    ios::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);} int main() {	// your code goes here    fast_io();    ll t;    cin>>t;    while(t--){        ll n,q;        cin>>n>>q;        vector<ll>a(n);        vector<ll>k(q);        for(ll i=0;i<n;i++) {cin>>a[i];}        for(ll i=0;i<q;i++) cin>>k[i];        vector<ll>mx(n);mx[0] = a[0];        for(ll i=1;i<n;i++){            mx[i] = max(mx[i-1],a[i]);        }        vector<ll>h(n);        h[0] = a[0];        for(ll i=1;i<n;i++){            h[i] = a[i]+h[i-1];        }        for(ll i=0;i<q;i++){            ll x = k[i];            ll ans =-1;            ll lo =0;            ll hi = n-1;            while(lo<=hi){                ll mid = lo + (hi-lo)/2;                if(mx[mid]<=x){                    ans = max(ans,mid);                    lo = mid+1;                }                else hi = mid-1;            }            if(ans==-1) cout<<0<<" ";            else cout<<h[ans]<<" ";        }        cout<<endl;    }}