#include <iostream>#include <vector>#include <numeric>#include <algorithm> using namespace std; // Standard GCD functionlong long gcd(long long a, long long b) {    while (b) {        a %= b;        swap(a, b);    }    return a;} bool solve() {    int n;    cin >> n;    vector<int> a(n);    for (int i = 0; i < n; i++) cin >> a[i];     // Try every possible pair as the first two elements    for (int i = 0; i < n; i++) {        for (int j = 0; j < n; j++) {            if (i == j) continue;             int current_gcd = gcd(a[i], a[j]);            if (current_gcd <= 2) {                // If the first pair works, check if we can complete the array                vector<bool> used(n, false);                used[i] = true;                used[j] = true;                int count = 2;                int g = current_gcd;                 while (count < n) {                    bool found = false;                    for (int k = 0; k < n; k++) {                        if (!used[k] && gcd(g, a[k]) <= count + 1) {                            g = gcd(g, a[k]);                            used[k] = true;                            count++;                            found = true;                            break;                         }                    }                    if (!found) break; // Cannot satisfy prefix condition for this i, j                }                 if (count == n) return true;            }        }    }    return false;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin >> t;    while (t--) {        if (solve()) cout << "Yes" << endl;        else cout << "No" << endl;    }    return 0;}