#include <bits/stdc++.h>using namespace std; void solve() {    int n;    cin >> n;    vector<long long> a(n);    long long total_sum = 0;    int neg_count = 0;    long long min_abs = LLONG_MAX;     for (int i = 0; i < n; i++) {        cin >> a[i];        if (a[i] < 0) neg_count++;                // Sum the absolute values        total_sum += abs(a[i]);                // Find the smallest absolute value in the array        min_abs = min(min_abs, abs(a[i]));    }     // If odd number of negatives, we must subtract twice the smallest     // absolute value (once to remove it from the sum, once to subtract it)    if (neg_count % 2 != 0) {        cout << total_sum - 2 * min_abs << endl;    } else {        cout << total_sum << endl;    }} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin >> t;    while (t--) {        solve();    }    return 0;}