#include <bits/stdc++.h>using namespace std;typedef long long ll;int MOD = 1e9 + 7; ll rs(ll l,ll r) {    if(l>r){        return 0;    }    // Using the Arithmetic Series formula: (n / 2) * (first + last)    return (l + r) * (r - l + 1) / 2;} ll unq(vector<ll> &a){    // prints only the unique elements    // removes the contigous duplicates and returns the new size    ll n = unique(a.begin(), a.end()) - a.begin();    return n;} ll __lcm(ll a, ll b){    return (a / __gcd(a, b)) * b;} void fast_io(){    ios::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);}  int main() {	// your code goes here ll t; cin>>t; while(t--){    ll n;    cin>>n;    vector<ll>a(n);    for(ll i=0;i<n;i++) cin>>a[i];   int l = 0, r = n - 1;    int mn = 1, mx = n;    while (l <= r) {        if (a[l] == mn) {            l++;            mn++;        } else if (a[l] == mx) {            l++;            mx--;        } else if (a[r] == mn) {            r--;            mn++;        } else if (a[r] == mx) {            r--;            mx--;        } else {            break;        }    }    if(l <= r){        cout << l + 1 << " " << r + 1 << endl;    } else{        cout << -1 << endl;    }     }}