#include <iostream>#include <vector> using namespace std;  int main() {    int t;    cin >> t;    while (t--) {          int n;    long long k;    cin >> n >> k;     vector<vector<int>> a(n, vector<int>(n));    for (int i = 0; i < n; i++) {        for (int j = 0; j < n; j++) {            cin >> a[i][j];        }    }     long long diff = 0;    for (int i = 0; i < n; i++) {        for (int j = 0; j < n; j++) {            // Compare cell with its 180-degree opposite            if (a[i][j] != a[n - 1 - i][n - 1 - j]) {                diff++;            }        }    }     // Each mismatched pair was counted twice in the loops above    diff /= 2;     if (k < diff) {        cout << "NO" << endl;    } else {        long long rem = k - diff;        if (n % 2 == 1) {            // Odd grid: Center cell can absorb any leftover ops            cout << "YES" << endl;        } else {            // Even grid: Leftovers must be even to flip/unflip            if (rem % 2 == 0) cout << "YES" << endl;            else cout << "NO" << endl;        }    }    }    return 0;}