#include <iostream>#include <string>#include <vector>#include <algorithm> using namespace std; void solve() {    string s;    cin >> s;    long long n = s.length();     // Check if there are any '1's at all    bool has_one = false;    for (char c : s) if (c == '1') has_one = true;    if (!has_one) {        cout << 0 << endl;        return;    }     // Handle the case where the entire string is '1's    int count_ones = 0;    for(char c : s) if(c == '1') count_ones++;    if (count_ones == n) {        cout << n * n << endl;        return;    }     // Find the longest contiguous block of '1's in a circular string    // We can simulate the circularity by doubling the string (s + s)    string doubled = s + s;    long long max_l = 0;    long long current_l = 0;        // We only need to check up to 2*n, but the longest block won't exceed n    for (char c : doubled) {        if (c == '1') {            current_l++;            max_l = max(max_l, current_l);        } else {            current_l = 0;        }    }        // Safety check: max_l cannot exceed n    max_l = min(max_l, n);     // Apply the formula: Area = floor((max_l + 1)/2) * ceil((max_l + 1)/2)    long long x = max_l + 1;    long long width = x / 2;    long long height = x - width;        cout << width * height << endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin >> t;    while (t--) {        solve();    }    return 0;}