#include <bits/stdc++.h>using namespace std; typedef long long ll;int MOD = 1e9 + 7; ll rs(ll l,ll r) {    if(l>r){        return 0;    }    // Using the Arithmetic Series formula: (n / 2) * (first + last)    return (l + r) * (r - l + 1) / 2;} ll unq(vector<ll> &a){    // prints only the unique elements    // removes the contigous duplicates and returns the new size    ll n = unique(a.begin(), a.end()) - a.begin();    return n;} ll __lcm(ll a, ll b){    return (a / __gcd(a, b)) * b;} void fast_io(){    ios::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);}  int main() {	// your code goes herefast_io;ll t;cin>>t;while(t--){    ll n,x,y;    cin>>n>>x>>y;    ll l  = __lcm(x,y);    ll sum = 0;    ll plus = (n/x)-(n/l);    ll minus = (n/y)-(n/l);    sum+=rs(n-plus+1,n);    sum-=rs(1,minus);    cout<<sum<<endl;}}