#include <bits/stdc++.h>using namespace std; int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin >> t;    while (t--) {        int n;        cin >> n;        vector<pair<int, int>> aa(n), bb(n), cc(n);                for (int i = 0; i < n; i++) { cin >> aa[i].first; aa[i].second = i; }        for (int i = 0; i < n; i++) { cin >> bb[i].first; bb[i].second = i; }        for (int i = 0; i < n; i++) { cin >> cc[i].first; cc[i].second = i; }         // Sort to get the largest values at the end        sort(aa.begin(), aa.end());        sort(bb.begin(), bb.end());        sort(cc.begin(), cc.end());         long long max_ans = 0;         // Iterate through the top 3 indices of each category        // Indices are n-1, n-2, n-3 because sort is ascending        for (int i = n - 1; i >= n - 3; i--) {            for (int j = n - 1; j >= n - 3; j--) {                for (int k = n - 1; k >= n - 3; k--) {                                        // Check if the days (original indices) are distinct                    if (aa[i].second != bb[j].second &&                         aa[i].second != cc[k].second &&                         bb[j].second != cc[k].second) {                                                long long current_sum = (long long)aa[i].first + bb[j].first + cc[k].first;                        max_ans = max(max_ans, current_sum);                    }                }            }        }        cout << max_ans << endl;    }    return 0;}