#include <bits/stdc++.h>using namespace std; typedef long long ll;int MOD = 1e9 + 7; ll unq(vector<ll> &a){    // prints only the unique elements    // removes the contigous duplicates and returns the new size    ll n = unique(a.begin(), a.end()) - a.begin();    return n;} ll __lcm(ll a, ll b){    return (a / __gcd(a, b)) * b;} void fast_io(){    ios::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);} int main() {	// your code goes herefast_io();ll t;cin>>t;while(t--){    ll n;    cin>>n;    vector<ll>a(n);    for(ll i=0;i<n;i++) cin>>a[i];    sort(a.begin(),a.end());    a.erase(unique(a.begin(), a.end()), a.end());    ll m = a.size();    ll ans = LLONG_MIN;    for(ll i=0;i<m;i++){        ll curr =0;        ll j=i;        while(j+1<m&&a[j+1]==a[j]+1){            curr++;            j++;        }        ans = max(ans,j-i+1);        i=j;    }    cout<<ans<<endl;}}