#include <iostream>#include <string>#include <vector> using namespace std; void solve() {    int n;    cin >> n;    string s;    cin >> s;     // Track original students and split zeros into segments    int original_ones = 0;    vector<int> zero_segments;    int current_len = 0;     for (int i = 0; i < n; i++) {        if (s[i] == '1') {            original_ones++;            zero_segments.push_back(current_len);            current_len = 0;        } else {            current_len++;        }    }    zero_segments.push_back(current_len);     // Case 1: The row is entirely empty    if (original_ones == 0) {        // Add two virtual zeros        cout << (n + 2) / 3 << "\n";        return;    }     // Case 2: Row has at least one student    int added_students = 0;    int num_segments = zero_segments.size();     for (int i = 0; i < num_segments; i++) {        int len = zero_segments[i];        if (i == 0 || i == num_segments - 1) {            // Boundary segments: add one virtual zero            added_students += (len + 1) / 3;        } else {            // Internal segments: floor(len / 3)            added_students += len / 3;        }    }     cout << original_ones + added_students << "\n";} int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        solve();    }    return 0;}