#include <bits/stdc++.h>using namespace std; typedef long long ll;int MOD = 1e9 + 7; ll rs(ll l,ll r) {    if(l>r){        return 0;    }    // Using the Arithmetic Series formula: (n / 2) * (first + last)    return (l + r) * (r - l + 1) / 2;} ll unq(vector<ll> &a){    // prints only the unique elements    // removes the contigous duplicates and returns the new size    ll n = unique(a.begin(), a.end()) - a.begin();    return n;} ll __lcm(ll a, ll b){    return (a / __gcd(a, b)) * b;} void fast_io(){    ios::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);}  long long get_range_sum(const vector<long long>& pref, int l, int r) {    if (l > r || l < 0) return 0;    return pref[r + 1] - pref[l];} int main() {	// your code goes here	fast_io;ll t;cin>>t;while(t--){     ll n;     cin>>n;     vector<ll>a(n);      vector<ll>b(n);      for(ll i=0;i<n;i++) cin>>a[i];for(ll i=0;i<n;i++) cin>>b[i];      sort(a.rbegin(), a.rend());     vector<ll>pref_b(n+1,0);    for (int i =0;i<n;i++){        pref_b[i+1] =pref_b[i]+b[i];    }     ll mx = 0;    for (int k =1;k<=n;k++){        ll d = pref_b[k];        if(d>n) break;        ll x=a[d-1];        mx=max(mx,(ll)k*x);    }    cout<<mx<<"\n";}}