#include <bits/stdc++.h>using namespace std; typedef long long ll;int MOD = 1e9 + 7; ll rs(ll l,ll r) {    if(l>r){        return 0;    }    // Using the Arithmetic Series formula: (n / 2) * (first + last)    return (l + r) * (r - l + 1) / 2;} ll unq(vector<ll> &a){    // prints only the unique elements    // removes the contigous duplicates and returns the new size    ll n = unique(a.begin(), a.end()) - a.begin();    return n;} ll __lcm(ll a, ll b){    return (a / __gcd(a, b)) * b;} void fast_io(){    ios::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);}  long long get_range_sum(const vector<long long>& pref, int l, int r) {    if (l > r || l < 0) return 0;    return pref[r + 1] - pref[l];} int main() {	// your code goes here	fast_io;ll t;cin>>t;while(t--){     ll n;        cin>>n;        vector<ll>k(n+1,0);        vector<ll>vals;        for (ll i=0;i<n;i++){            ll x;            cin>>x;            if(!k[x]){                k[x] = 1;                vals.push_back(x);             }        }        sort(vals.begin(), vals.end());        vector<ll>dp(n+1,-1);        dp[1] =0;        for (ll i =1;i<=n;i++){            if(dp[i]==-1) continue;            for(ll x:vals){                if(i*x>n) break;                if(dp[i*x]==-1)                    dp[i*x] =dp[i]+1;                else                    dp[i*x] =min(dp[i*x],dp[i]+1);            }        }        for(ll i =1;i<=n;i++){            if(i==1){                if(k[1]) cout<<1<<" ";                else cout<<-1<<" ";            }            else{                cout<<dp[i]<<" ";            }        }        cout<<endl;}}